INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Abacus', '', 1, 2, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Acid (vial)', 'As an action, you can splash the contents of this vial onto a creature within 5 feet of you or throw the vial up to 20 feet, shattering it on impact. In either case, make a ranged attack against a creature or object, treating the acid as an improvised weapon.  On a hit, the target takes 2d6 acid damage.', 1, 25, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Alchemist’s Fire (flask)', 'This sticky, adhesive fluid ignites when exposed to air. As an action, you can throw this flask up to 20 feet, shattering it on impact. Make a ranged attack against a creature or object, treating the alchemist’s fire as an improvised weapon. On a hit, the target takes 1d4 fire damage at the start of each of its turns. A creature can end this damage by using its action to make a DC 10 Dexterity check to extinguish the flames.', 1, 50, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (2, 'Arrows (20)', '', 1, 1, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (2, 'Blowgun needles (50)', '', 1, 1, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (2, 'Crossbow bolts (20)', '', 1, 1, 1.5, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (2, 'Sling bullets (20)', '', 2, 4, 1.5, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Antitoxin (vial)', 'A creature that drinks this vial of liquid gains advantage on saving throws against poison for 1 hour. It confers no benefit to undead or constructs.', 1, 50, 0, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (3, 'Crystal', 'Specially constructed to channel the power of arcane spells. A sorcerer, warlock, or wizard can use this as a spellcasting focus.', 1, 10, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (3, 'Orb', 'Specially constructed to channel the power of arcane spells. A sorcerer, warlock, or wizard can use this as a spellcasting focus.', 1, 20, 3, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (3, 'Rod','Specially constructed to channel the power of arcane spells. A sorcerer, warlock, or wizard can use this as a spellcasting focus.', 1, 10, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (3, 'Staff', 'Specially constructed to channel the power of arcane spells. A sorcerer, warlock, or wizard can use this as a spellcasting focus.', 1, 5, 4, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (3, 'Wand', 'Specially constructed to channel the power of arcane spells. A sorcerer, warlock, or wizard can use this as a spellcasting focus.', 1, 10, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Backpack', 'A backpack can hold up to 1 cubic foot or 30 pounds of gear. Additionally, you can also strap items, such a s a bedroll or a coil of rope, to the outside of a backpack.', 1, 2, 5, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Ball Bearings', 'As an action, you can spill these tiny metal balls from their pouch to cover a level, square area that is 10 feet on a side. A creature moving across the covered area must succeed on a DC 10 Dexterity saving throw or fall prone. A creature moving through the area at half speed doesn’t need to make the save.', 1, 1, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Barrel', 'A barrel can hold up to 40 gallons of liquid, or 4 cubic feet of solid material.', 1, 2, 70, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Basket', 'A basket can hold up to 2 cubic feet, or 40 pounds of gear.', 3, 4, 2, NULL);
