INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Blanket', '', 3, 5, 3, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Block and Tackle', 'A set of pulleys with a cable threaded through them and a hook to attach to objects, a block and tackle allows you to hoist up to four times the weight you can normally lift.', 1, 1, 5, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Book', 'A book might contain poetry, historical accounts, information pertaining to a particular field of lore, diagrams and notes on gnomish contraptions, or just about anything else that can be represented using text or pictures. A book of spells is a spellbook (described later in this section).', 1, 25, 5, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Bottle, glass', 'A bottle typically holds 1 1/2 pints of liquid.', 1, 2, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Box, alms', 'A small box containing donations. Holds up to 50 coins.', 1, 2, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Bucket', 'A bucket can hold up to 3 gallons of liquid, or a 1/2 cubic foot of solid material.', 2, 5, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Caltrops (bag of 20)', 'As an action, you can spread a bag of caltrops to cover a square area that is 5 feet on a side.  Any creature that enters the area must succeed on a DC 15 Dexterity saving throw or stop moving this turn and take 1 piercing damage. Taking this damage reduces the creature’s walking speed by 10 feet until the creature regains at least 1 hit point. A creature moving through the area at half speed doesn’t need to make the save.', 1, 1, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Candle', 'For 1 hour, a candle sheds bright light in a 5-­‐‑foot radius and dim light for an additional 5 feet.', 2, 1, 0, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Case, Crossbow Bolt', 'This wooden case can hold up to twenty crossbow bolts.', 1, 1, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Case, Map or Scroll', 'This cylindrical leather case can hold up to ten rolled-­‐‑up sheets of paper or five rolled-­‐‑up sheets of parchment.', 1, 1, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Censer', 'Incense is burned in this small metal bowl.', 1, 1, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Chain (10 feet)', 'A chain has 10 hit points. It can be burst with a successful DC 20 Strength check.', 1, 5, 10, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Chalk (1 piece)', '', 2, 1, 0, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Chest', 'A chest holds 12 cubic feet, or 300 pounds of gear.', 1, 5, 25, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Climber’s Kit', 'A climber’s kit includes special pitons, boot tips, gloves, and a harness. You can use the climber’s kit as an action to anchor yourself; when you do, you can’t fall more than 25 feet from the point where you anchored yourself, and you can’t climb more than 25 feet away from that point without undoing the anchor.', 1, 25, 12, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Clothes, common', '', 1, 5, 3, NULL);
