INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Spellbook', 'Essential for wizards, a spellbook is a leather-­‐‑bound tome with 100 blank vellum pages suitable for recording spells.', 1, 50, 3, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Spikes, iron (10)', '', 1, 1, 5, NULL);
