INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (4, 'Tankard', 'A tankard holds up to 1 pint of liquid.', 2, 2, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Tent, two-person', 'A simple and portable canvas shelter, a tent sleeps two.', 1, 2, 20, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Tinderbox', 'This small container holds flint, fire steel, and tinder (usually dry cloth soaked in light oil) used to kindle a fire. Using it to light a torch—or anything else with abundant, exposed fuel—takes an action. Lighting any other fire takes 1 minute.', 3, 5, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (1, 'Torch', 'A torch burns for 1 hour, providing bright light in a 20-­‐‑foot radius and dim light for an additional 20 feet. If you make a melee attack with a burning torch and hit, it deals 1 fire damage.', 2, 1, 1, NULL);
