INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (7, 'Alchemist’s supplies', 'These special tools include the items needed to pursue a craft or trade. Proficiency with a set of artisan’s tools lets you add your proficiency bonus to any ability checks you make using the tools in your craft.', 1, 50, 8, NULL);
