INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (7, 'Disguise kit', 'This pouch of cosmetics, hair dye, and small props lets you create disguises that change your physical appearance. Proficiency with this kit lets you add your proficiency bonus to any ability checks you make to create a visual disguise.', 1, 25, 3, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (7, 'Forgery kit', 'This small box contains a variety of papers and parchments, pens and inks, seals and sealing wax, gold and silver leaf, and other supplies necessary to create convincing forgeries of physical documents. Proficiency with this kit lets you add your proficiency bonus to any ability checks you make to create a physical forgery of a document.', 1, 15, 5, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (8, 'Dice set', 'If you are proficient with a gaming set, you can add your proficiency bonus to ability checks you make to play a game with that set.', 3, 1, 0, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (8, 'Playing card set', 'If you are proficient with a gaming set, you can add your proficiency bonus to ability checks you make to play a game with that set.', 3, 5, 0, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Bagpipes', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 30, 6, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Drum', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 6, 3, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Dulcimer', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 25, 10, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Flute', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 2, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Lute', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 35, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Lyre', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 30, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Horn', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 3, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Pan flute', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 12, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Shawm', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 2, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (9, 'Viol', 'If you have proficiency with a given musical instrument, you can add your proficiency bonus to any ability checks you make to play music with the instrument. A bard can use a musical instrument as a spellcasting focus.', 1, 30, 1, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (10, 'Herbalism kit', 'This kit contains a variety of instruments such as clippers, mortar and pestle, and pouches and vials used by herbalists to create remedies and potions. Proficiency with this kit lets you add your proficiency bonus to any ability checks you make to identify or apply herbs. Also, proficiency with this kit is required to create antitoxin and potions of healing.', 1, 5, 3, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (10, 'Navigator’s tools', 'This set of instruments is used for navigation at sea. Proficiency with navigator’s tools lets you chart a ship’s course and follow navigation charts. In addition, these tools allow you to add your proficiency bonus to any ability check you make to avoid getting lost at sea.', 1, 25, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (10, 'Poisoner’s kit', 'A poisoner’s kit includes the vials, chemicals, and other equipment necessary for the creation of poisons. Proficiency with this kit lets you add your proficiency bonus to any ability checks you make to craft or use poisons.', 1, 50, 2, NULL);
INSERT INTO item (itemType, name, description, coinID, cost, weight, bcUseAction) VALUES (10, 'Thieves’ tools', 'This set of tools includes a small file, a set of lock picks, a small mirror mounted on a metal handle, a set of narrow-­‐‑bladed scissors, and a pair of pliers. Proficiency with these tools lets you add your proficiency bonus to any ability checks you make to disarm traps or open locks.', 1, 25, 1 , NULL);
