INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (1, 'Aligned: The item seeks to defeat or destroy those of a diametrically opposed alignment. (Such an item is never neutral.)');
INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (2, 'Bane: The item seeks to defeat or destroy creatures of a particular kind, such as fiends, shapechangers, trolls, or wizards.');
INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (3, 'Protector: The item seeks to defend a particular race or kind of creature, such as elves or druids.');
INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (4, 'Crusader: The item seeks to defeat, weaken, or destroy the servants of a particular deity.');
