INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (6, 'Destroyer: The item craves destruction and goads its user to fight arbitrarily.');
INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (7, 'Glory Seeker: The item seeks renown as the greatest magic item in the world, by establishing its user as a famous or notorious figure.');
INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (8, 'Lore Seeker: The item craves knowledge or is determined to solve a mystery, learn a secret, or unravel a cryptic prophecy.');
INSERT INTO sentientMagicItemPurpose (roll, purpose) VALUES (9, 'Destiny Seeker: The item is convinced that it and its wielder have key roles to play in future events.');
