INSERT INTO monsterTrait (monsterID, name, description) VALUES (41, 'Fear Aura', ' Any creature hostile to the pit fiend that starts its turn within 20 Feet of the pit fiend must make a DC 21 Wisdom saving throw, unless the pit fiend is incapacitated. On a failed save, the creature is frightened until the start of its next turn. If a creature’s saving throw is successful, the creature is immune to the pit fiend’s Fear Aura for the next 24 hours.');
