INSERT INTO monsterTrait (monsterID, name, description) VALUES (102, 'Innate Spellcasting', ' The giant’s innate spellcasting ability is Charisma. It can innately cast the following spells, requiring no material components: At will: detect magic, fog cloud, light 3/day each: feather fall, fly, misty step, telekinesis 1/day each: control weather, gaseous form');
