INSERT INTO monsterTrait (monsterID, name, description) VALUES (120, 'Mimicry', ' The hag can mimic animal sounds and humanoid voices. A creature that hears the sounds can tell they are imitations with a successful DC 14 Wisdom (Insight) check.');
INSERT INTO monsterTrait (monsterID, name, description) VALUES (121, 'Innate Spellcasting', ' The hag’s innate spellcasting ability is Charisma (spell save DC 14, +6 to hit with spell attacks). She can innately cast the following spells, requiring no material components: At will: detect magic, magic missile 2/day each: plane shift (self only), ray of enfeeblement, sleep');
