INSERT INTO monsterTrait (monsterID, name, description) VALUES (214, 'False Appearance', ' While the tree remains motionless, it is indistinguishable from a normal tree.');
INSERT INTO monsterTrait (monsterID, name, description) VALUES (215, 'Description', ' An axe beak is a tall flightless bird with strong legs and a heavy, wedge-­‐‑shaped beak. It has a nasty disposition and tends to attack any unfamiliar creature that wanders too close.');
INSERT INTO monsterTrait (monsterID, name, description) VALUES (216, 'Pack Tactics', ' The baboon has advantage on an attack roll against a creature if at least one of the baboon’s allies is within 5 feet of the creature and the ally isn’t incapacitated.');
