INSERT INTO monsterReaction (monsterID, name, description) VALUES (94, 'Shriek', ' When bright light or a creature is within 30 Feet of the shrieker, it emits a shriek audible within 300 Feet of it. The shrieker continues to shriek until the disturbance moves out of range and for 1d4 of the shrieker’s turns afterward.');
INSERT INTO monsterReaction (monsterID, name, description) VALUES (106, 'Rock Catching', ' If a rock or similar object is hurled at the giant, the giant can, with a successful DC 10 Dexterity saving throw, catch the missile and take no bludgeoning damage from it.');
INSERT INTO monsterReaction (monsterID, name, description) VALUES (169, 'Split', ' When a pudding that is Medium or larger is subjected to lightning or slashing damage, it splits into two new puddings if it has at least 10 hit points. Each new pudding has hit points equal to half the original pudding’s, rounded down. New puddings are one size smaller than the original pudding.');
