INSERT INTO poisonType (name) VALUES ('Inhaled');
INSERT INTO poisonType (name) VALUES ('Injury');
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Assassin’s blood', 'A creature subjected to this poison must make a DC 10 Constitution saving throw. On a failed save, it takes 6 (1d12) poison damage and is poisoned for 24 hours. On a successful save, the creature takes half damage and isn’t poisoned.', 2, 1, 150);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Burnt othur fumes', 'A creature subjected to this poison must succeed on a DC 13 Constitution saving throw or take 10 (3d6) poison damage, and must repeat the saving throw at the start of each of its turns. On each successive failed save, the character takes 3 (1d6) poison damage.  After three successful saves, the poison ends.', 3, 1, 500);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Crawler mucus', 'This poison must be harvested from a dead or incapacitated crawler. A creature subjected to this poison must succeed on a DC 13 Constitution saving throw or be poisoned for 1 minute. The poisoned creature is paralyzed. The creature can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.', 1, 1, 200);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Drow poison', 'This poison is typically made only by the drow, and only in a place far removed from sunlight. A creature subjected to this poison must succeed on a DC 13 Constitution saving throw or be poisoned for 1 hour. If the saving throw fails by 5 or more, the creature is also unconscious while poisoned in this way. The creature wakes up if it takes damage or if another creature takes an action to shake it awake.', 4, 1, 200);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Essence of ether', 'A creature subjected to this poison must succeed on a DC 15 Constitution saving throw or become poisoned for 8 hours. The poisoned creature is unconscious. The creature wakes up if it takes damage or if another creature takes an action to shake it awake.', 3, 1, 300);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Malice', 'A creature subjected to this poison must succeed on a DC 15 Constitution saving throw or become poisoned for 1 hour. The poisoned creature is blinded.', 3, 1, 250);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Midnight tears', 'A creature that ingests this poison suffers no effect until the stroke of midnight. If the poison has not been neutralized before then, the creature must succeed on a DC 17 Constitution saving throw, taking 31 (9d6) poison damage on a failed save, or half as much damage on a successful one.', 2, 1, 1500);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Oil of taggit', 'A creature subjected to this poison must succeed on a DC 13 Constitution saving throw or become poisoned for 24 hours. The poisoned creature is unconscious. The creature wakes up if it takes damage.', 1, 1, 400);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Pale tincture', 'A creature subjected to this poison must succeed on a DC 16 Constitution saving throw or take 3 (1d6) poison damage and become poisoned. The poisoned creature must repeat the saving throw every 24 hours, taking 3 (1d6) poison damage on a failed save. Until this poison ends, the damage the poison deals can’t be healed by any means. After seven successful saving throws, the effect ends and the creature can heal normally.', 2, 1, 250);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Purple worm poison', 'This poison must be harvested from a dead or incapacitated purple worm. A creature subjected to this poison must make a DC 19 Constitution saving throw, taking 42 (12d6) poison damage on a failed save, or half as much damage on a successful one.', 4, 1, 2000);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Serpent venom', 'This poison must be harvested from a dead or incapacitated giant poisonous snake. A creature subjected to this poison must succeed on a DC 11 Constitution saving throw, taking 10 (3d6) poison damage on a failed save, or half as much damage on a successful one.', 4, 1, 200);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Torpor', 'A creature subjected to this poison must succeed on a DC 15 Constitution saving throw or become poisoned for 4d6 hours. The poisoned creature is incapacitated.', 2, 1, 600);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Truth serum', 'A creature subjected to this poison must succeed on a DC 11 Constitution saving throw or become poisoned for 1 hour. The poisoned creature can’t knowingly speak a lie, as if under the effect of a zone of truth spell.', 2, 1, 150);
INSERT INTO poison (name, description, poisonTypeID, coinID, price) VALUES ('Wyvern poison', 'This poison must be harvested from a dead or incapacitated wyvern. A creature subjected to this poison must make a DC 15 Constitution saving throw, taking 24 (7d6) poison damage on a failed save, or half as much damage on a successful one.', 4, 1, 1200);
